# -*- coding: UTF-8 -*-# 删除掉一些出现次数低，缺失比例大的字段，保留超过阈值的特征def remain_feat(df,thresh=0.9):    exclude_feats = []    print('----------移除数据缺失多的字段-----------')    print('移除之前总的字段数量',len(df.columns))    num_rows = df.shape[0]    for c in df.columns:        num_missing = df[c].isnull().sum()        if num_missing == 0:            continue        missing_percent = num_missing / float(num_rows)        if missing_percent > thresh:            exclude_feats.append(c)    print("移除缺失数据的字段数量: %s" % len(exclude_feats))    # 保留超过阈值的特征    feats = []    for c in df.columns:        if c not in exclude_feats:            feats.append(c)    print('剩余的字段数量',len(feats))    return feats